import React, {Component} from 'react';
import {Link} from 'react';
import './Welcome.scss';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

class Welcome extends Component{
    
    render(){
        return(
           <MenuContainer/>
        );
    }
}
///////////////////
// MenuContainer //
///////////////////
class MenuContainer extends React.Component {
  constructor(props) {
    super(props)
    
    this.state = {
      activeItem: '',
      activeItemPosition: 0,
      activeItemColor: '',
      menuItems: [
        { text: 'Insert' },
        { text: 'Display' },
      ],
    }
    
    this.handleClick = this.handleClick.bind(this)
  }
  
  handleClick(activeItem) {
    return e => {
      e.preventDefault()
    
      this.setState({
        activeItem,
        activeItemPosition: document.getElementById(activeItem).offsetTop,
        activeItemColor: window.getComputedStyle(document.getElementById(activeItem)).getPropertyValue('background-color'),
      })
      
    }
  }
  
  render() {
    const menuItems = this.state.menuItems.map(item => <MenuItem item={ item } handleClick={ this.handleClick }></MenuItem>)
    return (
      <div className='menu-container'>
        <span className='menu-item--active' style={{ top: this.state.activeItemPosition, backgroundColor: this.state.activeItemColor }}/>
        { menuItems }
      </div>
    )
  }
}

///////////////////
// MenuItem      //
///////////////////
function MenuItem(props) {
  return (
    <div 
      className='menu-item'
      id={ props.item.text }
      onClick={ props.handleClick(props.item.text) }
      component={Link} to="/">
    >
      { props.item.text.toUpperCase() }
      
    </div>
  )
}

export default Welcome;
