import React, { Component, PropTypes } from 'react';
import { Tabs } from 'react-tabs';
import { TabList } from 'react-tabs';
import { Tab } from 'react-tabs';
import { TabPanel } from 'react-tabs';
import InsertCostume from "../InsertCostume/InsertCostume";
import "../../styles/tabs.css";
import "./Insert.css";
import InsertUse from '../InsertUse/InsertUse';
import { Menu, Dropdown, Segment, MenuItem } from 'semantic-ui-react';
import { Link } from '@material-ui/core';

class Insert extends Component{

    state = { activeItem: 'costume' }
    
    handleItemClick = (e, { name }) => this.setState({ activeItem: name })

    render(){
        const { activeItem } = this.state

        return (
            <div>
                <Menu pointing secondary>
                <Menu.Item
                    name='costume'
                    content='Κοστούμι'
                    link='/insert/insertCostume'
                    active={activeItem === 'costume'}
                    onClick={this.handleItemClick}
                >
                    
                </Menu.Item>
                <Link to='/insertCostume' className="link"><Menu.Item
                    name='use'
                    content='Χρήση'
                    active={activeItem === 'use'}
                    onClick={this.handleItemClick}
                /></Link>
                </Menu>
            </div>
            
        )
    }
}

export default Insert;
