import React, {Component} from 'react';
import './DisplayCostumes.css';
import { InsertData } from '../../services/InsertData';

class DisplayCostume extends Component {
    constructor(props){
        super(props);
        this.state = {
            data:[],
            costumeData: '',
            redirectToReferrer: false,
        };
        this.onChange = this.onChange.bind(this);
        this.getCostumes = this.getCostumes.bind(this);
    }

    onChange(e){
        this.setState({costumeData:e.target.value});
        }

    getCostumes(){
        InsertData('costumes', this.state).then((result) => {
            let responseJson = result;
            if(responseJson.costumeData){
                sessionStorage.setItem("costumeData",JSON.stringify(responseJson));
                this.setState({data: responseJson.costumeData});
                console.log(this.state);
            }
            else{
                alert(result.error);
            }
        });
        this.getCostumes;
    }

    renderTableData(){
        return this.state.data.map((costume, index) => {
            const {id, name, description} = costume;
            return (
                <tr key={id}>
                    <td>{id}</td>
                    <td>{name}</td>
                    <td>{description}</td>
                </tr>
            )
        })
    }

    render(){
       return(
        <div>
        <h4 id='title'>Costumes Table</h4>
        {this.getCostumes}
        <input type="submit" className="button" value="Show" onClick={this.getCostumes}/>
        <table id='costumes'>
           <tbody>
              {this.renderTableData()}
           </tbody>
        </table>
     </div>
       )
    }
}

export default DisplayCostume;
